<?php require'config.php';require'models/sql_emoji.php';$a=glob('emoji/*');$e=getEmoji();if(sizeof($_POST)>0){foreach($a as$c){$c2=str_replace(['emoji/','.'],['','_'],$c);if(array_key_exists($c2, $_POST)&&strlen($_POST[$c2])>0&&strlen($_POST[$c2])<=10){$e[$c]=$_POST[$c2];saveEmoji($c,$_POST[$c2]);}}}?><!DOCTYPE html><html><head><title>Emoji</title></head><body><form method="POST" action=""><ul style="list-style:none"><?php foreach($a as$c){$c2=str_replace(['emoji/','.'],['','_'],$c);$s=(!empty($e[$c])?$e[$c]:'');echo sprintf('<li style="height:40px;margin:5px; float :left"><label><img src="%s" style="vertical-align:middle" /> : <input type="text" style="padding:2px;height:30px;" size="5" maxlength="10" name="%s" value="%s" /></label></li>', $c, $c2,$s);}?></ul><div style="clear:both;text-align:center;padding-top:55px"><input type="submit" style="height:40px;margin:5px; clear:both;" value="Enregistrer" /></div></form></body></html>